import './App.css';
import {useState , useEffect} from "react";
import Axios from "axios";
import {NavLink} from "react-router-dom";
import Pin from './Pin';
import Login from './Login';
import ans from './Login';

function App() {

  const [password , setPassword] = useState("");
  const [title , setTitle] = useState("");

  const addPassword = () => {
    Axios.post("http://localhost:3001/addpassword", {password: password , title: title , passcode: ans}).then(() => {
      window.location.reload(true);
    });

  };

  const[passList , setPassList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/showpass").then((response) => {
      setPassList(response.data);
    });
  },[]);

  const decryptPass = (encryption) => {
    Axios.post("http://localhost:3001/decryptpass" , {password: encryption.password , iv: encryption.iv}).then((response) => {
      setPassList(passList.map((val) => {
          return val.id === encryption.id ? {id: val.id , password: val.password , title:response.data , iv: val.iv}:val;
      }));
    });
  };

  return (
    <div className="App">
      <div className="Heading">
        To store Password Enter details below
      </div>
      <div className="Addpass">
          <input type="text" placeholder="Password" onChange={(event) => {
            setPassword(event.target.value);
          }}/>
          <input type="text" placeholder="Company" onChange={(event) => {
            setTitle(event.target.value);
          }}/>
          <button onClick={addPassword}> Add Password</button>
      </div>

      <div className="showPasswo">
          {passList.map((val) => {
            return <NavLink to="/Pin"> <div className="passcode" onClick={() => {
              decryptPass({password:val.password , iv:val.iv , id:val.id})
            }}> <h3> {val.title} </h3> </div> </NavLink>;
          })}
      </div>

    </div>
  );
}

export default App;
